// src/App.js
import React, { useState } from 'react';
import './App.css';
import Dropdown from './components/Dropdown';
import InputField from './components/InputField';
import DatePickerComponent from './components/DatePickerComponent';

function App() {
  const [formData, setFormData] = useState({
    userID: '',
    userName: '',
    shiftID: '',
    shiftName: '',
    facilityID: '',
    facilityName: '',
    contract: '',
    startDate: null,
    endDate: null,
    selectedOption1: '',
    selectedOption2: '',
    selectedOption3: '',
  });

  const [visibleInputs, setVisibleInputs] = useState({
    userID: true,
    userName: true,
    facilityID: true,
    facilityName: true,
    shiftID: true,
    shiftName: true,
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSelectChange = (index) => (event) => {
    const { value } = event.target;
    const optionKey = `selectedOption${index}`;
    setFormData((prevState) => ({
      ...prevState,
      [optionKey]: value,
    }));

    if (!visibleInputs[value]) {
      setVisibleInputs((prevState) => ({
        ...prevState,
        [value]: true,
      }));
    }
  };

  const handleDateChange = (date, name) => {
    setFormData((prevState) => ({
      ...prevState,
      [name]: date,
    }));
  };

  const handleRemoveInput = (field, optionField) => {
    setVisibleInputs((prevState) => ({
      ...prevState,
      [field]: false,
    }));
    setFormData((prevState) => ({
      ...prevState,
      [optionField]: '',
    }));
  };

  const handleSubmit = () => {
    console.log('Form Data Submitted: ', formData);
  };

  return (
    <div className="App">
      <div className="InputBox">
        <span className="TitleText">User Data</span>

        {/* Dropdown 1 */}
        <Dropdown
          name="selectedOption1"
          value={formData.selectedOption1}
          options={['userID', 'userName']}
          onChange={handleSelectChange(1)}
        />
        {formData.selectedOption1 === 'userID' && visibleInputs.userID && (
          <InputField
            name="userID"
            value={formData.userID}
            onChange={handleChange}
            placeholder="User ID"
            onRemove={() => handleRemoveInput('userID', 'selectedOption1')}
            showCancel={true}  // show cancel for userID
          />
        )}
        {formData.selectedOption1 === 'userName' && visibleInputs.userName && (
          <InputField
            name="userName"
            value={formData.userName}
            onChange={handleChange}
            placeholder="User Name"
            onRemove={() => handleRemoveInput('userName', 'selectedOption1')}
            showCancel={true}  // show cancel for userName
          />
        )}

        {/* Dropdown 2 */}
        <Dropdown
          name="selectedOption2"
          value={formData.selectedOption2}
          options={['facilityID', 'facilityName']}
          onChange={handleSelectChange(2)}
        />
        {formData.selectedOption2 === 'facilityID' && visibleInputs.facilityID && (
          <InputField
            name="facilityID"
            value={formData.facilityID}
            onChange={handleChange}
            placeholder="Facility ID"
            onRemove={() => handleRemoveInput('facilityID', 'selectedOption2')}
            showCancel={true}  // show cancel for facilityID
          />
        )}
        {formData.selectedOption2 === 'facilityName' && visibleInputs.facilityName && (
          <InputField
            name="facilityName"
            value={formData.facilityName}
            onChange={handleChange}
            placeholder="Facility Name"
            onRemove={() => handleRemoveInput('facilityName', 'selectedOption2')}
            showCancel={true}  // show cancel for facilityName
          />
        )}

        {/* Dropdown 3 */}
        <Dropdown
          name="selectedOption3"
          value={formData.selectedOption3}
          options={['shiftID', 'shiftName']}
          onChange={handleSelectChange(3)}
        />
        {formData.selectedOption3 === 'shiftID' && visibleInputs.shiftID && (
          <InputField
            name="shiftID"
            value={formData.shiftID}
            onChange={handleChange}
            placeholder="Shift ID"
            onRemove={() => handleRemoveInput('shiftID', 'selectedOption3')}
            showCancel={true}  // show cancel for shiftID
          />
        )}
        {formData.selectedOption3 === 'shiftName' && visibleInputs.shiftName && (
          <InputField
            name="shiftName"
            value={formData.shiftName}
            onChange={handleChange}
            placeholder="Shift Name"
            onRemove={() => handleRemoveInput('shiftName', 'selectedOption3')}
            showCancel={true}  // show cancel for shiftName
          />
        )}

        {/* Contract Input without cancel button */}
        <InputField
          name="contract"
          value={formData.contract}
          onChange={handleChange}
          placeholder="Contract"
          showCancel={false}  // do not show cancel for contract
        />

        {/* Date Pickers */}
        <DatePickerComponent
          selectedDate={formData.startDate}
          onChange={(date) => handleDateChange(date, 'startDate')}
          placeholder="Select Start Time"
        />
        <DatePickerComponent
          selectedDate={formData.endDate}
          onChange={(date) => handleDateChange(date, 'endDate')}
          placeholder="Select End Time"
        />

        {/* Submit Button */}
        <button onClick={handleSubmit} className="submitButton">
          Submit
        </button>
      </div>
    </div>
  );
}

export default App;
