import "./App.css";
import { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function App() {
  const [formData, setFormData] = useState({
    userID: "",
    userName: "",
    shiftID: "",
    shiftName: "",
    facilityID: "",
    facilityName: "",
    contract: "",
    startDate: null,
    endDate: null,
    selectedOption1: "",
    selectedOption2: "",
    selectedOption3: "",
  });

  const [visibleInputs, setVisibleInputs] = useState({
    userID: true,
    userName: true,
    facilityID: true,
    facilityName: true,
    shiftID: true,
    shiftName: true,
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSelectChange1 = (event) => {
    const { value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      selectedOption1: value,
    }));

    // Make sure corresponding input is visible
    if (!visibleInputs[value]) {
      setVisibleInputs((prevState) => ({
        ...prevState,
        [value]: true,
      }));
    }
  };

  const handleSelectChange2 = (event) => {
    const { value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      selectedOption2: value,
    }));

    // Make sure corresponding input is visible
    if (!visibleInputs[value]) {
      setVisibleInputs((prevState) => ({
        ...prevState,
        [value]: true,
      }));
    }
  };
  const handleSelectChange3 = (event) => {
    const { value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      selectedOption3: value,
    }));

    // Make sure corresponding input is visible
    if (!visibleInputs[value]) {
      setVisibleInputs((prevState) => ({
        ...prevState,
        [value]: true,
      }));
    }
  };

  const handleDateChange = (date, name) => {
    setFormData((prevState) => ({
      ...prevState,
      [name]: date,
    }));
  };

  const handleSubmit = () => {
    console.log("Form Data Submitted: ", formData);
  };

  const handleRemoveInput = (field) => {
    setVisibleInputs((prevState) => ({
      ...prevState,
      [field]: false,
    }));
  };

  return (
    <div className="App">
      <div className="InputBox">
        <span className="TitleText">User Data</span>

        {/* First dropdown */}
        <select
          name="selectedOption1"
          value={formData.selectedOption1}
          onChange={handleSelectChange1}
          className="InputText"
        >
          <option value="">Select an option</option>
          <option value="userID">User ID</option>
          <option value="userName">User Name</option>
        </select>

        {visibleInputs.userID && formData.selectedOption1 === "userID" && (
          <div className="inputWrapper">
            <input
              type="text"
              name="userID"
              value={formData.userID}
              onChange={handleChange}
              placeholder="User ID"
              className="InputText"
            />
            <button
              className="clearButton"
              onClick={() => handleRemoveInput("userID")}
            >
              ✖
            </button>
          </div>
        )}

        {visibleInputs.userName && formData.selectedOption1 === "userName" && (
          <div className="inputWrapper">
            <input
              type="text"
              name="userName"
              value={formData.userName}
              onChange={handleChange}
              placeholder="User Name"
              className="InputText"
            />
            <button
              className="clearButton"
              onClick={() => handleRemoveInput("userName")}
            >
              ✖
            </button>
          </div>
        )}

        {/* Second dropdown */}
        <select
          name="selectedOption2"
          value={formData.selectedOption2}
          onChange={handleSelectChange2}
          className="InputText"
        >
          <option value="">Select an option</option>
          <option value="facilityID">Facility ID</option>
          <option value="facilityName">Facility Name</option>
        </select>

        {visibleInputs.facilityID &&
          formData.selectedOption2 === "facilityID" && (
            <div className="inputWrapper">
              <input
                type="text"
                name="facilityID"
                value={formData.facilityID}
                onChange={handleChange}
                placeholder="Facility ID"
                className="InputText"
              />
              <button
                className="clearButton"
                onClick={() => handleRemoveInput("facilityID")}
              >
                ✖
              </button>
            </div>
          )}

        {visibleInputs.facilityName &&
          formData.selectedOption2 === "facilityName" && (
            <div className="inputWrapper">
              <input
                type="text"
                name="facilityName"
                value={formData.facilityName}
                onChange={handleChange}
                placeholder="Facility Name"
                className="InputText"
              />
              <button
                className="clearButton"
                onClick={() => handleRemoveInput("facilityName")}
              >
                ✖
              </button>
            </div>
          )}
        {/* Third dropdown */}
        <select
          name="selectedOption3"
          value={formData.selectedOption3}
          onChange={handleSelectChange3}
          className="InputText"
        >
          <option value="">Select an option</option>
          <option value="shiftID">Shift ID</option>
          <option value="shiftName">Shift Name</option>
        </select>

        {visibleInputs.shiftID && formData.selectedOption3 === "shiftID" && (
          <div className="inputWrapper">
            <input
              type="text"
              name="shiftID"
              value={formData.shiftID}
              onChange={handleChange}
              placeholder="Shift ID"
              className="InputText"
            />
            <button
              className="clearButton"
              onClick={() => handleRemoveInput("shiftID")}
            >
              ✖
            </button>
          </div>
        )}

        {visibleInputs.shiftName &&
          formData.selectedOption3 === "shiftName" && (
            <div className="inputWrapper">
              <input
                type="text"
                name="shiftName"
                value={formData.shiftName}
                onChange={handleChange}
                placeholder="Shift Name"
                className="InputText"
              />
              <button
                className="clearButton"
                onClick={() => handleRemoveInput("shiftName")}
              >
                ✖
              </button>
            </div>
          )}

        <input
          type="text"
          name="contract"
          value={formData.contract}
          onChange={handleChange}
          placeholder="Contract"
          className="InputText"
        />
        <DatePicker
          selected={formData.startDate}
          onChange={(date) => handleDateChange(date, "startDate")}
          showTimeSelect
          dateFormat="MMMM d, yyyy h:mm aa"
          placeholderText="Select Start Time"
          className="datePicker"
        />
        <DatePicker
          selected={formData.endDate}
          onChange={(date) => handleDateChange(date, "endDate")}
          showTimeSelect
          dateFormat="MMMM d, yyyy h:mm aa"
          placeholderText="Select End Time"
          className="datePicker"
        />
        <button onClick={handleSubmit} className="submitButton">
          Submit
        </button>
      </div>
    </div>
  );
}

export default App;
