import React, { useEffect, useState } from "react";
import "./App.css";
import Dropdown from "./Components/Dropdown";
import InputField from "./Components/InputField";
import DatePickerComponent from "./Components/DatePickerComponent";
import axios from "axios";

function App() {
  const fetchLists = async () => {
    const [userList, facilityList, shifts] = await Promise.all([
      axios.get("http://localhost:3000/users"),
      axios.get("http://localhost:3000/facility"),
      axios.get("http://localhost:3000/shifts"),
    ]);

    setUsers(userList);
    setFacility(facilityList);
    setShifts(shifts);
  };

  useEffect(() => {
    fetchLists().then(console.log).catch(console.log);
  }, []);

  const [users, setUsers] = useState([]);
  const [facility, setFacility] = useState([]);
  const [shifts, setShifts] = useState([]);

  const [formData, setFormData] = useState({
    userID: "",
    userName: "",
    shiftID: "",
    shiftName: "",
    facilityID: "",
    facilityName: "",
    contract: "",
    startDate: null,
    endDate: null,
    selectedOption1: "",
    selectedOption2: "",
    selectedOption3: "",
  });

  const [visibleInputs, setVisibleInputs] = useState({
    userID: true,
    userName: true,
    facilityID: true,
    facilityName: true,
    shiftID: true,
    shiftName: true,
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSelectChange = (index) => (event) => {
    const { value } = event.target;
    const optionKey = `selectedOption${index}`;
    setFormData((prevState) => ({
      ...prevState,
      [optionKey]: value,
    }));

    if (!visibleInputs[value]) {
      setVisibleInputs((prevState) => ({
        ...prevState,
        [value]: true,
      }));
    }
  };

  const handleDateChange = (date, name) => {
    setFormData((prevState) => ({
      ...prevState,
      [name]: date,
    }));
  };

  const handleRemoveInput = (field, optionField) => {
    setVisibleInputs((prevState) => ({
      ...prevState,
      [field]: false,
    }));
    setFormData((prevState) => ({
      ...prevState,
      [optionField]: "",
    }));
  };

  const handleSubmit = () => {
    console.log("Form Data Submitted: ", formData);
  };

  return (
    <div className="App">
      <div className="InputBox">
        <span className="TitleText">User Data</span>
        <div className="Component">
          <span className="ComponentText">User</span>
          <Dropdown
            name="selectedOption1"
            value={formData.selectedOption1}
            options={users}
            onChange={handleSelectChange(1)}
          />
        </div>

        <div className="Component">
          <span className="ComponentText">Facility</span>
          <Dropdown
            name="selectedOption2"
            value={formData.selectedOption2}
            options={facility}
            onChange={handleSelectChange(2)}
          />

        </div>
        <div className="Component">
          <span className="ComponentText">Shift</span>
          <Dropdown
            name="selectedOption3"
            value={formData.selectedOption3}
            options={shifts}
            onChange={handleSelectChange(3)}
          />
        </div>

        <InputField
          name="contract"
          value={formData.contract}
          onChange={handleChange}
          placeholder="Contract"
          showCancel={false}
        />

        <DatePickerComponent
          selectedDate={formData.startDate}
          onChange={(date) => handleDateChange(date, "startDate")}
          placeholder="Select Start Time"
        />
        <DatePickerComponent
          selectedDate={formData.endDate}
          onChange={(date) => handleDateChange(date, "endDate")}
          placeholder="Select End Time"
        />

        <button onClick={handleSubmit} className="submitButton">
          Submit
        </button>
      </div>
    </div>
  );
}

export default App;
