import "./App.css";
import { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function App() {
  const [formData, setFormData] = useState({
    userID: "",
    userName: "",
    shiftID: "",
    facilityID: "",
    contract: "",
    startDate: null,
    endDate: null,
    selectedOption: "userID", // Default to "userID" option
  });

  const [visibleInputs, setVisibleInputs] = useState({
    userID: true,
    userName: true,
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSelectChange = (event) => {
    const { value } = event.target;
    setFormData((prevState) => ({
      ...prevState,
      selectedOption: value,
    }));

    // If the selected option was previously removed, make it visible again
    if (!visibleInputs[value]) {
      setVisibleInputs((prevState) => ({
        ...prevState,
        [value]: true,
      }));
    }
  };

  const handleDateChange = (date, name) => {
    setFormData((prevState) => ({
      ...prevState,
      [name]: date,
    }));
  };

  const handleSubmit = () => {
    console.log("Form Data Submitted: ", formData);
  };

  const handleRemoveInput = (field) => {
    setVisibleInputs((prevState) => ({
      ...prevState,
      [field]: false,
    }));
  };

  return (
    <div className="App">
      <div className="InputBox">
        <span className="TitleText">User Data</span>
        <select
          name="selectedOption"
          value={formData.selectedOption}
          onChange={handleSelectChange}
          className="InputText"
        >
          <option value="userID">User ID</option>
          <option value="userName">User Name</option>
        </select>

        {visibleInputs.userID && formData.selectedOption === "userID" && (
          <div className="inputWrapper">
            <input
              type="text"
              name="userID"
              value={formData.userID}
              onChange={handleChange}
              placeholder="User ID"
              className="InputText"
            />
            <button
              className="clearButton"
              onClick={() => handleRemoveInput("userID")}
            >
              ✖
            </button>
          </div>
        )}

        {visibleInputs.userName && formData.selectedOption === "userName" && (
          <div className="inputWrapper">
            <input
              type="text"
              name="userName"
              value={formData.userName}
              onChange={handleChange}
              placeholder="User Name"
              className="InputText"
            />
            <button
              className="clearButton"
              onClick={() => handleRemoveInput("userName")}
            >
              ✖
            </button>
          </div>
        )}

        <input
          type="text"
          name="facilityID"
          value={formData.facilityID}
          onChange={handleChange}
          placeholder="Facility ID"
          className="InputText"
        />

        <input
          type="text"
          name="shiftID"
          value={formData.shiftID}
          onChange={handleChange}
          placeholder="Shift ID"
          className="InputText"
        />
        <input
          type="text"
          name="contract"
          value={formData.contract}
          onChange={handleChange}
          placeholder="Contract"
          className="InputText"
        />
        <DatePicker
          selected={formData.startDate}
          onChange={(date) => handleDateChange(date, "startDate")}
          showTimeSelect
          dateFormat="MMMM d, yyyy h:mm aa"
          placeholderText="Select Start Time"
          className="datePicker"
        />
        <DatePicker
          selected={formData.endDate}
          onChange={(date) => handleDateChange(date, "endDate")}
          showTimeSelect
          dateFormat="MMMM d, yyyy h:mm aa"
          placeholderText="Select End Time"
          className="datePicker"
        />
        <button onClick={handleSubmit} className="submitButton">
          Submit
        </button>
      </div>
    </div>
  );
}

export default App;
